import { StatusBar } from 'expo-status-bar';
import {Button, StyleSheet, Text, SafeAreaView, Alert} from 'react-native';
// import { SafeAreaView } from 'react-native-safe-area-context'
import {useState} from "react";


export default function App() {
    const [isVisibleStyles, setVisible] = useState(styles.textVisibleTrue);
    const handleButtonPress = () => {
        Alert.alert('Title', 'Examaple', [
            {
                text: 'Yes'
            },
            {
                text: 'No'
            }
        ]);
    }

    const toggleVisibility = () => {
        setVisible(prevState => {
            console.log(prevState);
            return prevState === styles.textVisibleTrue ? styles.textVisibleFalse: styles.textVisibleTrue;
        })
    }

  return (
    <SafeAreaView style={styles.container}>
        <Text numberOfLines={2}
              style={{...styles.text, ...isVisibleStyles}}
        >Ну привет, красавица моя❤️❤️❤️</Text>

        <Button title={'Hide'} onPress={toggleVisibility}/>

        <Button title={'Press me'} color={'black'} onPress={handleButtonPress}/>

        <StatusBar style="auto" />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    text: {
        color: 'green',
        fontWeight: 'bold',
        display: "flex"
    },
    textVisibleFalse: {
        display: 'none'
    },
    textVisibleTrue: {
        display: 'flex'
    }
});
