import {CheckBox} from "react-native-web";
import {ListItem} from "./ListItem";

export const TaskList = ({taskList}) => {
    return (

            <FlatList data={taskList} renderItem={({item}) => {
                const {title, completed, index} = item;
                return <ListItem>{title}</ListItem>
            }}
            scrollEnabled={true}
            />
    );
}

